import { Mutations } from "@/store/enums/EnumTypes";

const soundMutations = {
  [Mutations.prepareSoundEffect]: (state) => {
    console.log("prepareSoundEffect");
    if (!state.soundEffect) {
      const soundEffect = new Audio();
      soundEffect.autoplay = true;
      state.soundEffect = soundEffect;
    }
  },

  [Mutations.setSoundEffect]: (state) => {
    console.log("setSoundEffect");
    state.soundEffect.src =
      "data:audio/mpeg;base64,";
  },

  [Mutations.setSoundWaterDrop]: (state) => {
    console.log("setSoundWaterDrop");
    state.soundEffect.src = require("../../../assets/audio/waterdrop.mp3");
  },
};

export default soundMutations;
